final class EmojiDataManager: @unchecked Sendable {
    static let shared = EmojiDataManager()

    private init() {}

    /// Get all emojis matching a search query
    func searchEmojis(_ query: String) -> [String] {
        guard !query.isEmpty else { return [] }

        var results: [String] = []
        let lowercasedQuery = query.lowercased()

        // Search in all categories
        for category in EmojiGridCategory.allCases {
            if category == .recent { continue }
            results.append(contentsOf: category.emojis.filter { emoji in
                // Simple character match for now
                emoji.contains(lowercasedQuery) || emojiMatchesKeyword(emoji, keyword: lowercasedQuery)
            })
        }

        return Array(Set(results)).prefix(50).map { $0 }
    }

