// MARK: - Emoji Grid View

struct EmojiGridView: View {
    let config: EmojiGridConfig

    @State private var showFullBrowser = false
    @State private var searchText = ""
    @State private var selectedCategory: EmojiGridCategory = .smileys

    private let columns = Array(repeating: GridItem(.flexible(), spacing: 4), count: 8)

    var body: some View {
        VStack(spacing: 0) {
            if showFullBrowser {
                fullBrowserView
            } else {
                quickPickerView
            }
        }
    }

    // MARK: - Quick Picker View (Original compact view)

    private var quickPickerView: some View {
        ScrollView {
            VStack(alignment: .leading, spacing: 12) {
                // Recent Emojis Section
                if !config.recentEmojis.isEmpty {
                    VStack(alignment: .leading, spacing: 8) {
                        Text("RÃ©cents")
                            .font(.caption)
                            .foregroundStyle(.secondary)
                            .padding(.horizontal, 16)

                        LazyVGrid(columns: columns, spacing: 4) {
                            ForEach(config.recentEmojis.prefix(8), id: \.self) { emoji in
                                EmojiGridButton(emoji: emoji) {
                                    config.onSelect(emoji)
                                }
                            }
                        }
                        .padding(.horizontal, 12)
                    }
                }

                // Popular Emojis Section
                if !config.popularEmojis.isEmpty {
                    VStack(alignment: .leading, spacing: 8) {
                        Text("Populaires")
                            .font(.caption)
                            .foregroundStyle(.secondary)
                            .padding(.horizontal, 16)

                        LazyVGrid(columns: columns, spacing: 4) {
                            ForEach(config.popularEmojis.prefix(8), id: \.self) { emoji in
                                EmojiGridButton(emoji: emoji) {
                                    config.onSelect(emoji)
                                }
                            }
                        }
                        .padding(.horizontal, 12)
                    }
                }

                // Browse All Button
                Button {
                    withAnimation(.spring(response: 0.3, dampingFraction: 0.8)) {
                        showFullBrowser = true
                    }
                } label: {
                    HStack {
                        Image(systemName: "magnifyingglass")
                            .font(.system(size: 14, weight: .medium))

                        Text("Parcourir tous les emojis...")
                            .font(.subheadline)

                        Spacer()

                        Image(systemName: "chevron.right")
                            .font(.system(size: 12, weight: .semibold))
                    }
                    .foregroundColor(.blue)
                    .padding(.horizontal, 16)
                    .padding(.vertical, 10)
                    .background(
                        RoundedRectangle(cornerRadius: 10)
                            .fill(Color.blue.opacity(0.1))
                    )
                    .padding(.horizontal, 12)
                }
                .buttonStyle(PlainButtonStyle())
            }
            .padding(.vertical, 12)
        }
    }

    // MARK: - Full Browser View

    @ViewBuilder
    private var fullBrowserView: some View {
        VStack(spacing: 0) {
            // Back button + Search bar
            HStack(spacing: 8) {
                Button {
                    withAnimation(.spring(response: 0.3, dampingFraction: 0.8)) {
                        showFullBrowser = false
                        searchText = ""
                    }
                } label: {
                    Image(systemName: "chevron.left")
                        .font(.system(size: 16, weight: .semibold))
                        .foregroundColor(.blue)
                        .frame(width: 32, height: 32)
                }

                // Search Field
                HStack(spacing: 8) {
                    Image(systemName: "magnifyingglass")
                        .font(.system(size: 14))
                        .foregroundColor(.secondary)

                    TextField("Rechercher un emoji...", text: $searchText)
                        .font(.system(size: 14))
                        .autocorrectionDisabled()

                    if !searchText.isEmpty {
                        Button {
                            searchText = ""
                        } label: {
                            Image(systemName: "xmark.circle.fill")
                                .font(.system(size: 14))
                                .foregroundColor(.secondary)
                        }
                    }
                }
                .padding(.horizontal, 10)
                .padding(.vertical, 8)
                .background(Color(.systemGray6))
                .cornerRadius(10)
            }
            .padding(.horizontal, 12)
            .padding(.vertical, 8)

            // Category tabs (only when not searching)
            if searchText.isEmpty {
                ScrollView(.horizontal, showsIndicators: false) {
                    HStack(spacing: 4) {
                        ForEach(EmojiGridCategory.allCases.filter { $0 != .recent || !config.recentEmojis.isEmpty }) { category in
                            EmojiCategoryTab(
                                category: category,
                                isSelected: selectedCategory == category,
                                recentCount: category == .recent ? config.recentEmojis.count : nil
                            ) {
                                withAnimation(.easeInOut(duration: 0.2)) {
                                    selectedCategory = category
                                }
                            }
                        }
                    }
                    .padding(.horizontal, 12)
                }
                .padding(.vertical, 6)
            }

            Divider()

            // Emoji grid
            ScrollView {
                LazyVGrid(columns: columns, spacing: 4) {
                    ForEach(emojisToDisplay, id: \.self) { emoji in
                        EmojiGridButton(emoji: emoji) {
                            config.onSelect(emoji)
                        }
                    }
                }
                .padding(.horizontal, 12)
                .padding(.vertical, 8)
            }
        }
    }

    // MARK: - Computed Properties

    private var emojisToDisplay: [String] {
        if !searchText.isEmpty {
            return EmojiDataManager.shared.searchEmojis(searchText)
        }

        if selectedCategory == .recent {
            return config.recentEmojis
        }

        return selectedCategory.emojis
    }
}

// MARK: - Emoji Category Tab

private struct EmojiCategoryTab: View {
    let category: EmojiGridCategory
    let isSelected: Bool
    let recentCount: Int?
    let action: () -> Void

    var body: some View {
        Button(action: action) {
            HStack(spacing: 4) {
                Image(systemName: category.icon)
                    .font(.system(size: 12, weight: .medium))

                if let count = recentCount {
                    Text("\(count)")
                        .font(.system(size: 10, weight: .semibold))
                }
            }
            .foregroundColor(isSelected ? .white : .primary)
            .padding(.horizontal, 10)
            .padding(.vertical, 6)
            .background(
                RoundedRectangle(cornerRadius: 8)
                    .fill(isSelected ? Color.blue : Color(.systemGray6))
            )
        }
        .buttonStyle(PlainButtonStyle())
    }
}

// MARK: - Emoji Grid Button

private struct EmojiGridButton: View {
    let emoji: String
    let action: () -> Void

    var body: some View {
        Button(action: {
            // Haptic feedback
            let impact = UIImpactFeedbackGenerator(style: .light)
            impact.impactOccurred()

            action()
        }) {
            Text(emoji)
                .font(.system(size: 32))
                .frame(width: 44, height: 44)
        }
        .buttonStyle(EmojiGridButtonStyle())
    }
}

// MARK: - Button Style

private struct EmojiGridButtonStyle: ButtonStyle {
    func makeBody(configuration: Configuration) -> some View {
        configuration.label
            .scaleEffect(configuration.isPressed ? 1.3 : 1.0)
            .animation(.spring(response: 0.2, dampingFraction: 0.6), value: configuration.isPressed)
    }
}

