import SwiftUI

struct ReactionPickerView: View {
    // MARK: - Properties

    let onSelect: (String) -> Void

    @Environment(\.dismiss) private var dismiss: DismissAction
    @State private var showFullEmojiPicker = false

    // Common quick reactions
    private let quickReactions = ["ğŸ‘", "â¤ï¸", "ğŸ˜‚", "ğŸ˜®", "ğŸ˜¢", "ğŸ™", "ğŸ”¥", "ğŸ‰"]

    // MARK: - Body

    var body: some View {
        VStack(spacing: 0) {
            // Handle
            RoundedRectangle(cornerRadius: 2.5)
                .fill(Color(.systemGray4))
                .frame(width: 40, height: 5)
                .padding(.top, 12)
                .padding(.bottom, 20)

            // Title
            Text("React with an emoji")
                .font(.system(size: 17, weight: .semibold))
                .foregroundColor(.primary)
                .padding(.bottom, 24)

            // Quick Reactions
            LazyVGrid(
                columns: [
                    GridItem(.flexible()),
                    GridItem(.flexible()),
                    GridItem(.flexible()),
                    GridItem(.flexible())
                ],
                spacing: 16
            ) {
                ForEach(quickReactions, id: \.self) { emoji in
                    ReactionButton(emoji: emoji) {
                        selectReaction(emoji)
                    }
                }
            }
            .padding(.horizontal, 24)

            Divider()
                .padding(.vertical, 24)

            // More Emojis Button
            Button(action: {
                showFullEmojiPicker = true
            }) {
                HStack(spacing: 12) {
                    Image(systemName: "face.smiling")
                        .font(.system(size: 20))
                        .foregroundColor(.blue)

                    Text("More Emojis")
                        .font(.system(size: 17, weight: .medium))
                        .foregroundColor(.blue)

                    Spacer()

                    Image(systemName: "chevron.right")
                        .font(.system(size: 14, weight: .semibold))
                        .foregroundColor(.secondary)
                }
                .padding(.horizontal, 24)
                .padding(.vertical, 12)
            }
            .buttonStyle(.plain)

            Spacer(minLength: 20)
        }
        .background(Color(.systemBackground))
        .sheet(isPresented: $showFullEmojiPicker) {
            FullEmojiPickerView(onSelect: { emoji in
                selectReaction(emoji)
                showFullEmojiPicker = false
            })
        }
    }

    // MARK: - Helper Methods

    private func selectReaction(_ emoji: String) {
        // Haptic feedback
        let generator = UIImpactFeedbackGenerator(style: .light)
        generator.impactOccurred()

        onSelect(emoji)
        dismiss()
    }
}

// MARK: - Reaction Button

struct ReactionButton: View {
    let emoji: String
    let action: () -> Void

    @State private var isPressed = false

    var body: some View {
        Button(action: action) {
            Text(emoji)
                .font(.system(size: 48))
                .frame(width: 70, height: 70)
                .background(
                    Circle()
                        .fill(isPressed ? Color(.systemGray5) : Color(.systemGray6))
                )
                .scaleEffect(isPressed ? 0.9 : 1.0)
        }
        .buttonStyle(.plain)
        .simultaneousGesture(
            DragGesture(minimumDistance: 0)
                .onChanged { _ in
                    withAnimation(.easeInOut(duration: 0.1)) {
                        isPressed = true
                    }
                }
                .onEnded { _ in
                    withAnimation(.easeInOut(duration: 0.1)) {
                        isPressed = false
                    }
                }
        )
    }
}

// MARK: - Full Emoji Picker View

struct FullEmojiPickerView: View {
    let onSelect: (String) -> Void

    @Environment(\.dismiss) private var dismiss
    @State private var selectedCategory: EmojiCategory = .smileys

    var body: some View {
        NavigationView {
            VStack(spacing: 0) {
                // Category Tabs
                ScrollView(.horizontal, showsIndicators: false) {
                    HStack(spacing: 16) {
                        ForEach(EmojiCategory.allCases) { category in
                            CategoryTab(
                                category: category,
                                isSelected: selectedCategory == category,
                                action: {
                                    selectedCategory = category
                                }
                            )
                        }
                    }
                    .padding(.horizontal, 20)
                    .padding(.vertical, 12)
                }
                .background(Color(.systemGray6))

                // Emoji Grid
                ScrollView {
                    LazyVGrid(
                        columns: Array(repeating: GridItem(.flexible()), count: 8),
                        spacing: 12
                    ) {
                        ForEach(selectedCategory.emojis, id: \.self) { emoji in
                            Button(action: {
                                onSelect(emoji)
                            }) {
                                Text(emoji)
                                    .font(.system(size: 32))
                                    .frame(width: 44, height: 44)
                            }
                            .buttonStyle(.plain)
                        }
                    }
                    .padding(16)
                }
            }
            .navigationTitle("Choose Emoji")
            .navigationBarTitleDisplayMode(.inline)
            .toolbar {
                ToolbarItem(placement: .navigationBarTrailing) {
                    Button("Cancel") {
                        dismiss()
                    }
                }
            }
        }
    }
}

// MARK: - Category Tab

struct CategoryTab: View {
    let category: EmojiCategory
    let isSelected: Bool
    let action: () -> Void

    var body: some View {
        Button(action: action) {
            VStack(spacing: 4) {
                Text(category.icon)
                    .font(.system(size: 24))

                Text(category.rawValue)
                    .font(.system(size: 11, weight: .medium))
                    .foregroundColor(isSelected ? .blue : .secondary)
            }
            .padding(.horizontal, 8)
            .padding(.vertical, 4)
            .background(
                RoundedRectangle(cornerRadius: 8)
                    .fill(isSelected ? Color.blue.opacity(0.1) : Color.clear)
            )
        }
        .buttonStyle(.plain)
    }
}

